export default function validateInfo(values) {
    let errors ={}

    if(!values.monthlyIncome) {
        errors.monthlyIncome = 'Monthly income is required'
    }
    else if(values.monthlyIncome<500000) {
        errors.monthlyIncome = 'The minimum monthly income should be 500000'
    }

    if(!values.requestedAmount) {
        errors.requestedAmount = 'Requested amount is required'

    }

    if(!values.loanTerm) {
        errors.loanTerm = 'Requested amount is required'

    }

    if(!values.children) {
        errors.children = 'Requested amount is required'

    }
    if(!values.coapplicant) {
        errors.coapplicant = 'Requested amount is required'

    }


    return errors;
}
